setInterval(function(){$(".countdown-data").each(function(){var s=$(this).data("end"),t=new Date,e=s-Math.floor(t.getTime()/1e3);hours=minutes=seconds=0;if(e>0){var n=parseInt(e,10);hours=Math.floor(n/3600)%24,minutes=Math.floor(n/60)%60,seconds=n%60,seconds<10&&(seconds="0"+seconds),minutes<10&&(minutes="0"+minutes)}$(this).find(".hours").text(hours<10?"0"+hours:hours),$(this).find(".minutes").text(minutes),$(this).find(".seconds").text(seconds)})},1e3);